/**
 * Represents all of the data you can get from node_modules...
 * The "needsModule" is the value returned from `versionMap`:
 * - It's Key is always the version used for the installed `libraryName`
 * - wants are not included in this interface.
 */
export interface KeplerModuleInfo {
    needsModule: ModulesDeclared;
    versionMap: Record<string, ModulesDeclared>;
    privilegeMap: Record<string, string[]>;
}
export interface KeplerModuleLookupInfo {
    versionMap: Record<string, ModulesDeclared>;
    includesModules: string[];
}
export interface ManifestDefinition {
    needs: string[];
    wants: string[];
    privileges: string[];
    keplerModuleMap: Record<string, Record<SemVerString, ModuleString>>;
}
export declare type SemVerString = string;
export declare type ModuleString = string;
export declare type ModulesDeclared = string | string[];
export declare type PrivilegeString = string;
export interface KeplerCompatibilityOverride {
    needsVersion: SemVerString;
    wantsRange: string;
}
export interface LibraryDependencyOverride extends KeplerCompatibilityOverride {
    libraryName: string;
}
export interface KeplerCompatibilityMap {
    [key: string]: KeplerCompatibilityInputData;
}
export interface KeplerCompatibilityInputData {
    key: string;
    npmLibraryName: string;
    npmMajorVersion: string;
    versionMap: Record<SemVerString, ModulesDeclared>;
    privilegeMap: Record<SemVerString, PrivilegeString[]>;
    needsModule: ModulesDeclared;
    wantsModule?: ModulesDeclared;
    wantsRange?: SemVerString;
    libraryDependencies?: LibraryDependencyOverride[];
}
export interface CompatibilityFile {
    versions: Record<SemVerString, ModulesDeclared>;
    privileges?: Record<SemVerString, PrivilegeString[]>;
}
export interface NpmLibraryKeplerModuleCompatibilityRecord {
    [npmLibraryName: string]: {
        needs: SemVerString;
        wants: SemVerString;
    };
}
export interface KeplerBackwardCompatibility {
    kepler?: {
        backwardCompatibility: NpmLibraryKeplerModuleCompatibilityRecord;
    };
}
export interface AllowList {
    [libraryName: string]: {
        [majorVersion: string]: boolean;
    };
}
