import NativeDialogManagerKepler from '../NativeModules/specs/NativeDialogManagerKepler';

function emptyCallback() {}

module.exports = {
  alertWithArgs: function(args, callback) {
    // TODO(5998984): Polyfill it correctly with DialogManagerKepler
    if (!NativeDialogManagerKepler) {
      return;
    }

    NativeDialogManagerKepler.showAlert(
      args,
      emptyCallback,
      callback || emptyCallback,
    );
  },
};
