import type * as React from 'react';
import {Constructor} from '../../../types/private/Utilities';
import {NativeMethods} from '../../../types/public/ReactNativeTypes';
import {ColorValue} from '../../StyleSheet/StyleSheet';
import {ViewProps} from '../View/ViewPropTypes';

/**
 * ProgressBarKepler is used to expose a UI similar to ProgressBarAndroid
 * on the Kepler platform and minimize changes to RN developer codebase.
 */
export interface ProgressBarKeplerProps extends ViewProps {
  /**
     * Style of the ProgressBar. One of:
         Horizontal
         Normal (default)
         Small
         Large
         Inverse
         SmallInverse
         LargeInverse
     */
  styleAttr?:
    | 'Horizontal'
    | 'Normal'
    | 'Small'
    | 'Large'
    | 'Inverse'
    | 'SmallInverse'
    | 'LargeInverse'
    | undefined;

  /**
   * If the progress bar will show indeterminate progress.
   * Note that this can only be false if styleAttr is Horizontal.
   */
  indeterminate?: boolean | undefined;

  /**
   * The progress value (between 0 and 1).
   */
  progress?: number | undefined;

  /**
   * Whether to show the ProgressBar (true, the default) or hide it (false).
   */
  animating?: boolean | undefined;

  /**
   * Color of the progress bar.
   */
  color?: ColorValue | undefined;

  /**
   * Used to locate this view in end-to-end tests.
   */
  testID?: string | undefined;
}

/**
 * React component that wraps the Kepler-only `ProgressBar`. This component is used to indicate
 * that the app is loading or there is some activity in the app.
 */
declare class ProgressBarKeplerComponent extends React.Component<ProgressBarKeplerProps> {}
declare const ProgressBarKeplerBase: Constructor<NativeMethods> &
  typeof ProgressBarKeplerComponent;
/**
 * ProgressBarKepler types should be separated from
 * 'react-native' imports, as is being done for ProgressBarAndroid and others.
 */
export class ProgressBarKepler extends ProgressBarKeplerBase {}
