import { MediaKeysRequirement } from './EncryptedMediaInterface';
/**
 * @link https://www.w3.org/TR/media-capabilities/#keysystemtrackconfiguration
 */
export interface KeySystemTrackConfiguration {
    /**
     * The robustness member represents a robustness level as described in EME apis.
     *
     * @link https://www.w3.org/TR/media-capabilities/#dom-keysystemtrackconfiguration-robustness
     */
    robustness: string;
    /**
     * The encryptionScheme member represents an encryptionScheme as described in EME apis.
     */
    encryptionScheme?: string;
}
/**
 * @link https://w3c.github.io/media-capabilities/#mediacapabilitieskeysystemconfiguration
 */
export interface MediaCapabilitiesKeySystemConfiguration {
    /**
     * The keySystem member represents a keySystem name as described in EME apis.
     *
     * @link https://w3c.github.io/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-keysystem
     */
    keySystem: string;
    /**
     * The initDataType member represents a single value from the initDataTypes sequence described in EME apis.
     *
     * @link https://w3c.github.io/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-initdatatype
     */
    initDataType: string;
    /**
     * The distinctiveIdentifier member represents a distinctiveIdentifier requirement as described in EME apis.
     *
     * @link https://w3c.github.io/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-distinctiveidentifier
     */
    distinctiveIdentifier?: MediaKeysRequirement;
    /**
     * The persistentState member represents a persistentState requirement as described in EME apis.
     *
     * @link https://www.w3.org/TR/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-persistentstate
     */
    persistentState?: MediaKeysRequirement;
    /**
     *  The sessionTypes member represents a sequence of required sessionTypes as described in EME apis.
     *
     * @link https://www.w3.org/TR/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-sessiontypes
     */
    sessionTypes: Array<string>;
    /**
     * The audio member represents a @type {KeySystemTrackConfiguration} associated with the @type {AudioConfiguration}.
     *
     * @link https://www.w3.org/TR/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-audio
     */
    audio: KeySystemTrackConfiguration;
    /**
     * The video member represents a @type {KeySystemTrackConfiguration} associated with the @type {VideoConfiguration}.
     *
     * @link https://www.w3.org/TR/media-capabilities/#dom-mediacapabilitieskeysystemconfiguration-audio
     */
    video: KeySystemTrackConfiguration;
}
