/**
 * @file IAlexaManager.ts
 * @description This module exposes Alexa Manager APIs to apps
 *
 * @todo Revisit if these apis need to return success / failure status to apps
 * @todo Revisit if any of these methods are asyncronous and may return Promise
 * @todo Check with Speech middleware if they want to always bulk update their capabilities
 * @todo Check with KSM how they merge static and dynamic capabilites
 * @todo See if we want to avoid Alexa keyword to accomodate other services
 * @todo Check with KSM why they need directive paramter in sendResponse API
 */
import { AlexaCapability, AlexaDirective, AlexaDirectiveListenerCallbacks, AlexaErrorResponseType, AlexaManagerStatus, AlexaStateChangeCauseType } from '../AlexaManagerTypes';
export interface IAlexaManager {
    /**
     * @brief This method will start AlexaManager and register listeners for directives and state changes.
     * After this call, directives and state requests may be received.
     *
     * @returns completion status.
     */
    start: () => AlexaManagerStatus;
    /**
     * @brief This method will stop AlexaManager, unregister listeners for directives and state changes.
     * After this call, no more callbacks to IAlexaManager will be received.
     *
     * @returns completion status.
     */
    stop: () => AlexaManagerStatus;
    /**
     * @brief This method will register listeners for directives with the AlexaManager.
     *
     * @param callbacks callbacks to be driven for onPreHandleDirective, onHandleDirective, and onCancelDirective events.
     * @returns completion status of registering directive listeners.
     */
    registerDirectiveListener: (callbacks: AlexaDirectiveListenerCallbacks) => AlexaManagerStatus;
    /**
     * @brief This method will register a listener for providing state with the AlexaManager.
     *
     * @param avsNamespace namespace identifying the state provider metadata
     * @param name name identifying the state provider metadata
     * @returns completion status of registering the state provider listener.
     */
    registerStateProvider: (avsNamespace: string, name: string) => AlexaManagerStatus;
    /**
     * @brief This method will unregister state provider with the AlexaManager.
     * State cannot be added in reportStateChange function payload after this call.
     *
     * @param avsNamespace namespace identifying the state provider metadata.
     * @param name name identifying the state provider metadata.
     * @returns completion status of unregistering the state provider listener.
     */
    unregisterStateProvider: (avsNamespace: string, name: string) => AlexaManagerStatus;
    /**
     * @brief This method sends a response for a directive.
     *
     * @param directive referencing directive event for which response has been processed.
     * The directive's JSON payload will be as defined by AVS for that particular directive.
     * @param payload payload of directive response.
     * @returns completion status of sending response.
     */
    sendResponse: (directive: AlexaDirective, payload: string) => AlexaManagerStatus;
    /**
     * @brief This method sends a deferred response for a directive.
     *
     * @param directive referencing directive event for which response has been processed.
     * @param expectedDeferredInSeconds expected duration of deferral in seconds.
     * @returns completion status of sending deferred response.
     */
    sendDeferredResponse: (directive: AlexaDirective, expectedDeferralInSeconds: number) => AlexaManagerStatus;
    /**
     * @brief This method sends an error response for a directive.
     *
     * @param directive referencing directive event for which response has been processed.
     * @param errorType errorType matching AlexaErrorResponseType.
     * @param errorMessage string message.
     * @returns completion status of sending error response.
     */
    sendErrorResponse: (directive: AlexaDirective, errorType: AlexaErrorResponseType, errorMessage: string) => AlexaManagerStatus;
    /**
     * @brief This method can be called to proactively report Alexa state change by the app.
     *
     * @param avsNamespace namespace identifying the state provider metadata
     * @param name name identifying the state provider metadata
     * @param state Json string containing state change payload for property.
     * @param cause Alexa state change cause matching AlexaStateChangeCauseType.
     * @returns completion status of reporting the state change.
     */
    reportStateChange: (avsNamespace: string, name: string, state: string, cause: AlexaStateChangeCauseType) => AlexaManagerStatus;
    /**
     * @brief This method allows setting new capabilities for an app.
     *
     * @param capabilities array of AlexaCapability objects.
     * @returns completion status of setting capabilities.
     */
    setCapabilities: (capabilities: AlexaCapability[]) => AlexaManagerStatus;
    /**
     * @brief This method allows generating generic AVS event
     *
     * @param namespace of avs event
     * @param name of avs event
     * @param payload of avs event
     * @param dialogRequestId of avs event.  May be blank (Optional).
     * @param interactionId of avs event.  May be blank (Optional).
     */
    sendAvsEvent: (avsNamespace: string, name: string, payload: string, dialogRequestId?: string, interactionId?: string) => AlexaManagerStatus;
}
