import type * as React from 'react';
import {ColorValue} from '../StyleSheet/StyleSheet';
import {TouchableNativeFeedbackProps} from './Touchable/TouchableNativeFeedback';
import {TouchableOpacityProps} from './Touchable/TouchableOpacity';

export interface ButtonProps
  extends Pick<
    TouchableNativeFeedbackProps & TouchableOpacityProps,
    | 'accessibilityLabel'
    | 'accessibilityState'
    | 'hasTVPreferredFocus'
    | 'nextFocusDown'
    | 'nextFocusForward'
    | 'nextFocusLeft'
    | 'nextFocusRight'
    | 'nextFocusUp'
    | 'testID'
    | 'disabled'
    | 'onPress'
    | 'touchSoundDisabled'
    // amznmod_react - add focusable actions
    | 'onFocus'
    | 'onBlur'
    | 'style'
    | 'focusable'
    | 'isFocusableInTouchMode'
    // amznmod_react - add voice types
    | 'voiceOrdinal'
    | 'voiceEntityType'
    | 'voiceExternalIds'
    | 'voiceAlternativeNames'
    // amznmod_react - add ariaPosInSet, alias for voiceOrdinal
    | 'ariaPosInSet'
    // amznmod_react - add ariaLabel, alias for accessibilityLabel
    | 'ariaLabel'
    // amznmod_react - add keplerAtlLabels (deprecated), alias for voiceAlternativeNames
    | 'keplerAtlLabels'
    // amznmod_react - add keplerAltLabels, alias for voiceAlternativeNames
    | 'keplerAltLabels'
    // add alexaEntityType, alias for voiceEntityType
    | 'alexaEntityType'
    // add alexaExternalIds
    | 'alexaExternalIds'
    // add enableSynchronousFocusEventsKepler
    | 'enableSynchronousFocusEventsKepler'
  > {
  /**
   * Text to display inside the button. On Android the given title will be converted to the uppercased form.
   */
  title: string;

  /**
   * Color of the text (iOS), or background color of the button (Android).
   */
  color?: ColorValue | undefined;
}

export class Button extends React.Component<ButtonProps> {}
