'use strict';

// amznmod_react - Do not import InitializeCore for headless.
// InitializeCore evaluates UI-related code when imported and thus should not be imported by headless bundle.
// But since @react-native/metro-config forces importing InitializeCore to any bundle that imports "react-native"
// or "@amzn/react-native-kepler", the content of InitializeCore.js is moved to InitializeCoreImpl.js which is
// imported here only if not headless.
if (typeof __KEPLER_HEADLESS__ === 'undefined') {
  require('./InitializeCoreImpl');
} else {
  console.debug('Skip InitializeCore for headless.');
}
