import {type EventSubscription} from '../vendor/emitter/EventEmitter';
import NativeEventSubscription from './NativeNetworkingAndroid'

export default class KeplerEventSubscription implements EventSubscription {
    nativeEventSubscription: NativeEventSubscription;
    eventType: string;
    /**
     * @param {NativeEventSubscription} Native event subscription
     */
    constructor(eventType: string,  nativeEventSubscription: NativeEventSubscription) {
        this.eventType = eventType;
        this.nativeEventSubscription = nativeEventSubscription;
    }

    /**
     * Removes this subscription from the subscriber that controls it.
     */
    remove() {
        this.nativeEventSubscription.remove();
    }
}
