/**
 * @file PlayerSession.ts
 * @description This module exposes player session apis to apps
 * Check @link {@interface PlayerSessionCallback} module for the definition of DMP / CMP / EMP apps
 *
 * @todo This interface in future will be refactored as needed to support multiple active playback sessions in a single App for use cases such as Picture in picture. At the moment only one active session is supported
 * @todo Revisit if these apis need to return success / failure status to apps
 * @todo There are few more apis that might get added to this interface after aligning with other teams
 */
import { PlaybackState } from './PlayerSessionTypes';
import { PlayerSessionCallback } from './PlayerSessionCallback';
/**
 * @interface IPlayerSession
 * @description - This interface exposes various apis for the apps to integrate voice experience.
 * DMP apps don't need to call any of these apis to get the basic voice experience for play back functionality.
 * @todo Add setTrackMetaData() API for EMP applications
 */
export interface IPlayerSession {
    /**
     * @function create To create a player session. This function is a pass through and creates a native player session.
     * EMP and DMP apps do NOT need to use this API. A player session is created by default after creating @amzn/react-native-kepler/Libraries/VideoPlayer component. Even if EMP and DMP uses this api, it is a no-op
     * CMP apps use this api to create Sessions
     * For EMP apps, a session is created as soon as app launched
     * @return True if Player session is created else false
     */
    create: () => boolean;
    /**
     * @function destroy To destroy a player session. This function is a pass through and destroys a native player session.
     * EMP and DMP apps do NOT need to use this API. A player session is destroyed by default after destroying @amzn/react-native-kepler/Libraries/VideoPlayer component. Even if EMP and DMP apps uses this api, it is a no-op
     * CMP apps use this api to destroy Sessions that the apps created
     * For EMP apps, a session is destroyed after the app is destroyed
     * @todo This API in future will change as needed to support multiple active playback sessions in a single App such as Picture in picture use cases. At the moment only one active session is supported
     * @todo Do we need to give control to apps to destroy. If we do there are concerns of destroying when it should not
     */
    destroy: () => void;
    /**
     * @function setPlayerSessionCallback To set an object of player session call backs such as onPlay / onPause
     * These callbacks are called after receiving voice events from middleware
     * @param playerSessionCallback A player session call back object which has apps implemented callbacks of @link {@type PlayerSessionCallback}
     */
    setPlayerSessionCallback: (playerSessionCallback: PlayerSessionCallback) => void;
    /**
     * @function setPlaybackState
     * @description setPlaybackState For apps to send current playback state on demand. This api is in response to @link {@function onRequestPlayerPlaybackStateInfo}
     * @param playbackState - Current play back state in apps @link {@enum PlaybackState}
     * @returns True if successfully sent playback state to native else false
     */
    setPlaybackState: (playbackState: PlaybackState) => boolean;
    /**
     * @function setLoginStatus
     * @description setLoginStatus For apps to send current log in status state on demand. This api is in response to @link {@function onRequestLogInStatus}. This api is only used by EMP apps. This api needs to be called on demand as well as pro-actively when the app log in status changes
     * @param userName - Current user. If @param loginStatus is false, then @param userName could be empty.
     * @param loginStatus - Current log in status of the user
     * @returns None
     */
    setLoginStatus?: (userName: string, loginStatus: boolean) => void;
}
